// Obtener valor original
let vistasOriginal = $input.first().json.views; // Ej: "2,166 views"

// Eliminar comas y la palabra 'views'
let vistasLimpias = vistasOriginal.replace(/,/g, '').replace(/\s*views/i, '');

// Convertir a n√∫mero
let vistasNumericas = parseInt(vistasLimpias);

return {
  json: {
    vistasNumericas
  }
};
